select 

from open_apply_info
where  unit_code = #{unitCode,jdbcType=VARCHAR} and idempotent_id = #{idempotentId,jdbcType=VARCHAR}

   select 

   from open_apply_info  

   	 	source =1 and ( sync is null or sync = 0) and (next_sync_time is null or next_sync_time <= now())  

				and unit_code = #{unitCode,jdbcType=VARCHAR}


   order by id asc
   limit #{start} , #{limit}

    select 

    from open_apply_info
    where is_delet = 0 


 				and unit_code = #{unitCode,jdbcType=VARCHAR}


 				and apply_code = #{applyCode,jdbcType=VARCHAR}


				and match_code = #{matchCode,jdbcType=VARCHAR}


				and match_group_code = #{matchGroupCode,jdbcType=VARCHAR}


				and field_code = #{fieldCode,jdbcType=VARCHAR}


				and event_code = #{eventCode,jdbcType=VARCHAR}


				and leader_phone = #{leaderPhone,jdbcType=VARCHAR}


    order by id asc
    limit #{start} , #{limit}

    select 

    from open_apply_info t1

       	   t1.is_delete=0 and t1.apply_code in ( select apply_code from open_player t2 where t2.is_delete = 0 and t2.player_phone = #{playerPhone, jdbcType=VARCHAR} )

 				and t1.unit_code = #{unitCode,jdbcType=VARCHAR}


 				and t1.apply_code = #{applyCode,jdbcType=VARCHAR}


				and t1.match_code = #{matchCode,jdbcType=VARCHAR}


				and t1.match_group_code = #{matchGroupCode,jdbcType=VARCHAR}


				and t1.field_code = #{fieldCode,jdbcType=VARCHAR}



				and t1.event_code = #{eventCode,jdbcType=VARCHAR}


				and t1.leader_phone = #{leaderPhone,jdbcType=VARCHAR}


    order by id asc
    limit #{start} , #{limit}

SELECT a.apply_time,a.unit_code,count(1) AS counts from open_apply_info a,open_player b where a.apply_code = b.apply_code and a.unit_code is not null and a.unit_code != '' and (a.is_delet = 0 or a.is_delet is null)

	  and DATE(a.apply_time) = #{applyTime,jdbcType=VARCHAR}

GROUP BY a.unit_code

      select a.match_code,a.match_name,a.apply_time,a.unit_code,count(1) as counts from open_apply_info a,open_player b where a.apply_code = b.apply_code and a.unit_code is not null and a.unit_code != '' and (a.is_delet = 0 or a.is_delet is null)   

	      and DATE(a.apply_time) = #{applyTime,jdbcType=VARCHAR} 


      	 and (a.source = 1)


      	 and (a.source != 1)

      GROUP BY a.match_code order by apply_time desc;

