mapper.select:
      select  
      from report_match_finish where 1=1 

	      and match_code = #{matchCode,jdbcType=VARCHAR} 


      	 and part_time_day = #{partTimeDay,jdbcType=VARCHAR}

      select match_code as matchCode, match_name as matchName,SUM(finish_count) as finishCount from report_match_finish


		      and match_code = #{matchCode,jdbcType=VARCHAR} 


      GROUP BY match_code

      select match_code as matchCode,unit_code as unitCode,SUM(organizer_count) as organizerCount from report_match_finish where 1=1  

	      and match_code = #{matchCode,jdbcType=VARCHAR} 

      GROUP BY unit_code

      select SUM(finish_total_count) as finishCont from report_match_finish where 1=1

	      and part_time_day >= #{startTime,jdbcType=VARCHAR} 


      	 and part_time_day <=  #{endTime,jdbcType=VARCHAR}
