  unit_code, unit_name, enroll_total_count, 
  organizer_count, official_count, apply_time, 
  apply_time_day, gmt_create, gmt_modified
  )
values (#{id,jdbcType=INTEGER}, #{matchCode,jdbcType=VARCHAR}, #{matchName,jdbcType=VARCHAR}, 
  #{unitCode,jdbcType=VARCHAR}, #{unitName,jdbcType=VARCHAR}, #{enrollTotalCount,jdbcType=INTEGER}, 
  #{organizerCount,jdbcType=INTEGER}, #{officialCount,jdbcType=INTEGER}, #{applyTime,jdbcType=TIMESTAMP}, 
  #{applyTimeDay,jdbcType=TIMESTAMP}, #{gmtCreate,jdbcType=TIMESTAMP}, #{gmtModified,jdbcType=TIMESTAMP}
  )

