mapper.select:
      select  
      from report_unit_finish where 1=1 

	      and unit_code = #{unitCode,jdbcType=VARCHAR} 


      	 and part_time_day = #{partTimeDay,jdbcType=VARCHAR}

      select SUM(finish_cont) as finishCont from report_unit_finish where 1=1

	      and part_time_day >= #{startTime,jdbcType=VARCHAR} 


      	 and part_time_day <=  #{endTime,jdbcType=VARCHAR}

      select part_time,unit_code,unit_name,SUM(finish_cont) as finishCount from report_unit_finish where 1=1

	      and part_time_day >= #{startTime,jdbcType=VARCHAR} 


      	 and part_time_day <=  #{endTime,jdbcType=VARCHAR}  


      	 and unit_name LIKE #{likeParams,jdbcType=VARCHAR} 


      	 and unit_code in 

             #{item}  


      GROUP BY unit_code

      select unit_code as unitCode,unit_name as unitName,SUM(finish_cont) as finishCount from report_unit_finish


		      and unit_code = #{unitCode,jdbcType=VARCHAR} 


      GROUP BY unit_code
